SELECT FROM t_cost_info ci WHERE userId=#{userId,jdbcType=BIGINT} AND isDeleted=0 AND isHidden=0 AND costCreateTime like concat(#{month,jdbcType=VARCHAR},'%') AND EXISTS ( SELECT 1 FROM t_cost_tag ct WHERE ct.costId=ci.id AND ct.isDeleted=0
AND ct.tagId IN
#{tagId, jdbcType=BIGINT}
AND 1=2
)

SELECT 
FROM t_cost_info ci
WHERE userId=#{userId,jdbcType=BIGINT}
AND isDeleted=0 AND isHidden=0 AND inOutType='支出'
AND costCreateTime like concat(#{month,jdbcType=VARCHAR},'%')

AND NOT EXISTS (
    SELECT 1 FROM t_cost_tag ct WHERE ct.costId=ci.id AND ct.isDeleted=0
    AND ct.tagId IN

        #{tagId, jdbcType=BIGINT}

)
